package week1.day2assignment;

public class findOddNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     
		int num=10;
		int i;
		for(i=1;i<=num;i++) 
		{
						
			if (i%2==0) 
			{
				System.out.println("The number is even " +i);
				
			}
			else 
			{
				System.out.println("The number is odd "+i);
			}
		}
		
	}

}
